import React from "react"
import Square from "./Square"

export default function Boxes() {
    const [squares, setSquares] = React.useState(["white", "blue", "yellow", "green"])

    function handleClick (){

        // if the first square is white, set all of them to black 
        // else set all the squares to white 

        // setSquares("white")
        if(squares[0] === "white"){
            setSquares(["black", "black", "black", "black"])
        }
        else {
            setSquares(["white", "white", "white", "white"])
        }
    }
    function party (){
        setSquares(function(prevState){
            /// any code can go here 
            return ["purple", "purple", prevState[2], prevState[3]]
        })
    }
    function leftBlue (){
        setSquares(function(prevState){
            return 
        })
    }

    return(
    <div className="main">
            <h1 className="title">DJ REACT</h1>
        <div className="wrapper">
            <Square color={squares[0]}/>
            <Square color={squares[1]}/>
            <Square color={squares[2]} class={"boxc"}/>
            <Square color={squares[3]}/>
            {/* <div className="boxa"> 
            </div>
                <div className ="boxb">
                </div>
                    <div className="boxc">
                    </div>
                        <div className="boxd">
                        </div> */}
                <div className="buttons">
                    <button className="djSmall" onClick = {handleClick}>DJ SMALL</button>
                    <button className="djParty" onClick = {party}>DJ PARTY</button>
                    <button className="leftBlue" onClick = {leftBlue}>LEFT BLUE</button>
                    <button className="rightBlue">RIGHT BLUE</button>
                    <button className="bigOne">BIG DJ ONE</button>
                    <button className="bigTwo">BIG DJ TWO</button>
                    <button className="bigThree">BIG DJ THREE</button>
                    <button className="bigFour">BIG DJ FOUR</button>
                    <button className="noise">MAKE NOISE</button>

                </div>
        </div>
    </div>
    )
}
