import React from "react"

export default function DiceBox(){
    const [numbers, setNumbers] = React.useState([1, 2, 3, 4, 5, 6])

    function handleClick() {
        setNumbers((prevState) => {
            return [Math.floor(Math.random() * 6[0],[1],[2],[3],[4])]
        })
    }

    return (
         <div>
             <h2>This is my DiceBox component</h2>
             <button className="button" onClick = {handleClick}>Random Number</button>
        </div>
    )
}
